/*字体*/
@font-face {
    font-family:'arzhu';
    src:url('/font/SJ.ttf'); /* 修改成你的字体 */
    font-display:swap
}
h1#site-title {
    font-family:arzhu!important
}
span#subtitle {
    font-family:arzhu!important
}
a#site-name {
    font-family:arzhu!important
}

@font-face {
    font-family:'Regular';
    src:url('/font/Regular.ttf'); /* 修改成你的字体 */
    font-display:swap
}

/*鼠标样式*/
body {
    cursor: url(https://cdn.jsdelivr.net/gh/caibhh/CDN/img/blog/default.cur), default
}
#nav .site-page:hover {
    cursor:url(https://cdn.jsdelivr.net/gh/caibhh/CDN/img/blog/pointer.cur), auto
}
div#bbTimeList {
    cursor:url(https://cdn.jsdelivr.net/gh/caibhh/CDN/img/blog/pointer.cur), auto
}
a, img {
    cursor: url(https://cdn.jsdelivr.net/gh/caibhh/CDN/img/blog/pointer.cur), pointer
}
/*a:hover {
    cursor:url(https://cdn.jsdelivr.net/gh/caibhh/CDN/img/blog/pointer.cur), auto
}*/
i:hover {
    cursor:url(https://cdn.jsdelivr.net/gh/caibhh/CDN/img/blog/pointer.cur), auto
}

/*搜索页圆角*/
#local-search .search-dialog {
    border-radius: 12px;
}

/*右侧按钮圆角*/
#rightside>div>a, #rightside>div>button {
    border-radius: 12px;
}

/*分页圆角*/
span.page-number.current {
    border-radius: 8px;
}

/* 文章页H1-H6图标样式效果 · 风车*/
h1::before, h2::before, h3::before, h4::before, h5::before, h6::before {
    -webkit-animation: ccc 1.6s linear infinite ;
    animation: ccc 1.6s linear infinite ;
}
@-webkit-keyframes ccc {
    0% {
        -webkit-transform: rotate(0deg);
        transform: rotate(0deg)
    }
    to {
        -webkit-transform: rotate(-1turn);
        transform: rotate(-1turn)
    }
}
@keyframes ccc {
    0% {
        -webkit-transform: rotate(0deg);
        transform: rotate(0deg)
    }
    to {
        -webkit-transform: rotate(-1turn);
        transform: rotate(-1turn)
    }
}
#content-inner.layout h1::before {
    color: #ef50a8 ;
    margin-left: -1.55rem;
    font-size: 1.3rem;
    margin-top: -0.23rem;
}
#content-inner.layout h2::before {
    color: #fb7061 ;
    margin-left: -1.35rem;
    font-size: 1.1rem;
    margin-top: -0.12rem;
}
#content-inner.layout h3::before {
    color: #ffbf00 ;
    margin-left: -1.22rem;
    font-size: 0.95rem;
    margin-top: -0.09rem;
}
#content-inner.layout h4::before {
    color: #a9e000 ;
    margin-left: -1.05rem;
    font-size: 0.8rem;
    margin-top: -0.09rem;
}
#content-inner.layout h5::before {
    color: #57c850 ;
    margin-left: -0.9rem;
    font-size: 0.7rem;
    margin-top: 0.0rem;
}
#content-inner.layout h6::before {
    color: #5ec1e0 ;
    margin-left: -0.9rem;
    font-size: 0.66rem;
    margin-top: 0.0rem;
}
#content-inner.layout h1:hover, #content-inner.layout h2:hover, #content-inner.layout h3:hover, #content-inner.layout h4:hover, #content-inner.layout h5:hover, #content-inner.layout h6:hover {
    color: #49b1f5 ;
}
#content-inner.layout h1:hover::before, #content-inner.layout h2:hover::before, #content-inner.layout h3:hover::before, #content-inner.layout h4:hover::before, #content-inner.layout h5:hover::before, #content-inner.layout h6:hover::before {
    color: #49b1f5 ;
    -webkit-animation: ccc 3.2s linear infinite ;
    animation: ccc 3.2s linear infinite ;
}
/* 页面设置icon转动速度调整 */
#rightside_config i.fas.fa-cog.fa-spin {
    animation: fa-spin 5s linear infinite ;
}

/*右侧时钟去除背景*/
#aside-content.aside-content > .sticky_layout > .card-widget.card-clock > .card-glass > .card-background {
    background-color: unset ;
    box-shadow: unset ;
}

